namespace WordFinderAPI.Controllers{        [ApiController]    [Route("api/[controller]")]    public class WordFinderController : ControllerBase    {        // POST: api/WordFinder/find-words        [HttpPost("find-words")]        public ActionResult<List<string>> FindWordsInMatrix([FromBody] WordFinderRequest request)        {            // Validate that a matrix and wordstream are provided            if (request.Matrix == null || request.Matrix.Count == 0)            {                return BadRequest("Matrix cannot be null or empty.");            }            if (request.WordStream == null || request.WordStream.Count == 0)            {                return BadRequest("Word stream cannot be null or empty.");            }            try            {                // Instantiate WordFinder AFTER receiving the correct matrix                var wordFinder = new WordFinder(request.Matrix); // Aqu√≠ ya se pasa la matriz completa                // Perform the search with the provided wordstream                var foundWords = wordFinder.Find(request.WordStream);                // Return the list of found words                return Ok(foundWords.ToList());            }            catch (Exception ex)            {                // Handle any exceptions that might occur                return BadRequest($"An error occurred: {ex.Message}");            }        }    }}